import Todo from './Todo';

// this statement connects our react state with the redux store
import {connect} from 'react-redux';

export class TodoList extends Component {
    // initial react state for the application
    constructor(props) {
        super(props);
    }


    render() {
        debugger
        let todos = this.state.todos.map((e, ind) => (
            <Todo task={e} index={ind}/>
        ))
        return (
            <div>
                <ul>
                    {todos}
                </ul>

            </div>
        )
    }
}

// exporting connect with the component as an arg
export default connect()(TodoList);
