// load in the actions from the actionCreator file
import { ADD_TODO, REMOVE_TODO } from './actionCreator';

// set the initial redux state of the application
const initialState = {
    todos: [],
    id: 0
}


// root reducer function that manages state
export default function rootReducer(state = initialState, action) {
    // switch statement based off of action type's value
    switch (action.type) {
        // Add
        // create copy of state
        // increment copy ID values
        // return new obj w/ all state contents & a new object who's values come in from the params
        case ADD_TODO:
            let newAddedState = { ...state };
            newAddedState.id++;
            return {
                ...newState,
                todos: [...newAddedState.todos, { task: action.task, id: newAddedState.id }]
            }

        case REMOVE_TODO:
            let newFilteredState = { ...state };
            let newTodos = newFilteredState.todos.filter((val => val.id !== action.id))
            return {
                ...newFilteredState,
                newTodos
            }

        default:
            return state
    }
}
